import styled, { keyframes } from "styled-components";
import { useState } from "react";

const Container = styled.div`
  width: 100%;
  height: auto;
  display: flex;
  justify-content: center;

  box-shadow: 5px 5px 5px 5px var(--line-gray);
  padding: 1rem;
  gap: 1rem;
`;

const StartDiv = styled.div`
  width: 100%;
  height: auto;
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const UlKeyframe = keyframes`
from {
  opacity: 0;
  transform: translate3d(0, -10%, 0);
}

to {
  opacity: 1;
  transform: translate3d(0, 0, 0);
}
}
`;

const SelectBox = styled.div`
  width: 100%;
  height: 100px;
  overflow-y: scroll;
  display: flex;
  flex-direction: column;
  gap: 1rem;
  background: white;
  padding: 1rem;
  animation: ${UlKeyframe} 0.2s ease-in-out;

  button {
    width: 100%;
    height: auto;
    cursor: pointer;
  }
`;

const TimeInput = styled.input`
  width: 100%;
  padding: 1rem;
  outline: none;
  text-align: center;
  transition: background-color 0.5s ease;

  &:disabled {
    background: var(--whiteblue);
  }
`;

// type OnChange = {
//   onChange: string | ((e: React.ChangeEvent<HTMLInputElement>) => void) | null;
// };

export default function TimePicker() {
  const [picktime, setPicktime] = useState<{ start: string; end: string }>({
    start: "",
    end: "",
  });

  const onChange = (
    e: React.MouseEvent<HTMLElement, MouseEvent>,
    start: boolean,
  ) => {
    const value = (e.target as HTMLInputElement).value;
    if (start) {
      setPicktime({ ...picktime, start: `${value}` });
    } else {
      setPicktime({ ...picktime, end: `${value}` });
    }
  };

  const timeArr = Array.from({ length: 24 }, (_, i) => i + 1);
  return (
    <Container>
      <StartDiv>
        <TimeInput
          disabled={true}
          type="text"
          readOnly
          placeholder="시작 시간"
          value={`${picktime.start}`}
        ></TimeInput>
        <SelectBox>
          {timeArr.map((item: number, key: number) => {
            return (
              <button
                key={key}
                onClick={(e) => onChange(e, false)}
                value={item}
              >{`${item} : 00`}</button>
            );
          })}
        </SelectBox>
      </StartDiv>

      <StartDiv>
        <TimeInput
          disabled={true}
          type="text"
          readOnly
          placeholder="시작 시간"
          value={`${picktime.end}`}
        ></TimeInput>

        <SelectBox>
          {timeArr.map((item: number, key: number) => {
            return (
              <button
                key={key}
                onClick={(e) => onChange(e, false)}
              >{`${item} : 00`}</button>
            );
          })}
        </SelectBox>
      </StartDiv>
    </Container>
  );
}
