import styled from "styled-components";
import Day from "../Components/Month-day";
const WeekBox = styled.ul`
  width: 100%;
  flex-grow: 1;
  display: flex;
`;

type DataHoliday = {
  dateName: string;
  isHoliday: string;
};
interface Prop {
  isHoliday: number[];
  holiday: DataHoliday[];
  date: Date;
}

export default function Week(props: Prop) {
  const { isHoliday, holiday, date } = props;
  const monthList = (nowDate: Date) => {
    const nowYear = nowDate.getFullYear();
    const nowMonth = nowDate.getMonth();

    const dayOneWeek = new Date(nowYear, nowMonth, 1).getDay();

    let weekArr: number[] = [];
    const result: [number[]] = [[]];
    const dayArr: number[] = [];
    result.pop();

    const nowMonthEnd = new Date(nowYear, nowMonth + 1, 0).getDate();
    const prevMonthEnd = new Date(nowYear, nowMonth, 0).getDate();

    for (let i = dayOneWeek - 1; i >= 0; i--) {
      dayArr.push(new Date(nowYear, nowMonth - 1, prevMonthEnd - i).getDay());
      weekArr.push(0);
    }

    for (let i = 1; i <= nowMonthEnd; i++) {
      dayArr.push(new Date(nowYear, nowMonth, i).getDay());
      weekArr.push(new Date(nowYear, nowMonth, i).getDate());

      if (weekArr.length === 7) {
        result.push(weekArr);
        weekArr = [];
      }
    }

    if (weekArr.length) {
      while (weekArr.length < 7) {
        weekArr.push(0);
      }
      result.push(weekArr);
    }

    return { result, dayArr, nowMonth, nowYear };
  };
  const { result, nowMonth, nowYear, dayArr } = monthList(date);
  const allDay: [number[]] = result;

  return (
    <>
      {allDay.map((item: number[], idx: number) => {
        return (
          <WeekBox key={idx}>
            {item.map((day: number, key: number) => {
              // const index: number = isHoliday.indexOf(day);
              return (
                <Day
                  day={day}
                  getday={dayArr[key]}
                  nowYear={nowYear}
                  month={nowMonth + 1}
                  key={key}
                  holiday={holiday[0]}
                ></Day>
              );
            })}
          </WeekBox>
        );
      })}
    </>
  );
}
