import styled from "styled-components";
import Day from "../Components/Month-day";
const WeekBox = styled.ul`
  width: 100%;
  flex-grow: 1;
  display: flex;
`;

export default function Week(props: { week: number[]; idx: number }) {
  return (
    <WeekBox>
      {week.map((day: number, key: number) => {
        const index: number = isHoliday.indexOf(day);
        return (
          <Day
            day={day}
            getday={dayArr[key]}
            nowYear={nowYear}
            month={nowMonth + 1}
            key={key}
            holiday={index === -1 ? undefined : holiday[index]}
          ></Day>
        );
      })}
    </WeekBox>
  );
}
