import styled from "styled-components";
import { useLayoutEffect } from "react";
import axios from "axios";
import { ReactComponent as Arrow } from "../Img/ep_arrow-right-bold.svg";
interface MyDate {
  setDate: React.Dispatch<React.SetStateAction<Date>>;
  setHoliDate: React.Dispatch<React.SetStateAction<Holiday[] | undefined>>;
  date: Date;
}

const MonthBox = styled.div`
  width: 100%;
  height: 5%;
  display: flex;
  justify-content: center;
  align-items: center;

  div {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 2rem;
    .timeBox {
      display: flex;
      justify-content: center;
      align-items: center;
      gap: 0;
      span {
        font-size: var(--header-size);
        font-weight: 600;
        min-width: 120px;
        text-align: center;
      }
    }
  }
`;

const ArrowBtn = styled.button`
  display: flex;
  justify-content: center;

  &:hover {
    path {
      fill: var(--skyblue);
    }
  }
`;

const Arrowimg = styled(Arrow)`
  width: 100%;
  height: 100%;
`;

const ReverseArrow = styled(Arrowimg)`
  transform: scaleX(-1);
`;
export default function MonthHead(props: MyDate) {
  const { date, setDate } = props;

  const monthArr: string[] = [
    "Jaunary",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  const getHoliday = () => {
    const strMonth =
      String(date.getMonth()).length < 2
        ? "0" + String(date.getMonth() + 1)
        : String(date.getMonth() + 1);

    const url = `http://apis.data.go.kr/B090041/openapi/service/SpcdeInfoService/getHoliDeInfo?ServiceKey=${process.env.REACT_APP_HOLIDAY_KEY}&solYear=${date.getFullYear()}&solMonth=${strMonth}`;
    console.log(date);
    axios
      .get(url)
      .then((res) => {
        const holidataArr = [].concat(res.data.response.body.items.item);
        if (holidataArr[0] === undefined) holidataArr.pop();
        setHoliDate(holidataArr);
      })
      .catch((err) => console.log("err", err));
  };
  useLayoutEffect(() => {
    getHoliday();
  }, [date]);

  const changeMonth = (count: number) => {
    const nowDate = new Date(date.getTime());
    let [yearCount, monthCount] = [0, 0];

    if (nowDate.getMonth() + (count % 12) + 1 > 12) {
      yearCount++;
      monthCount = (count % 12) - 12;
    } else if (nowDate.getMonth() + (count % 12) + 1 <= 0) {
      yearCount--;
      monthCount = (count % 12) + 12;
    } else {
      monthCount = count % 12;
    }

    nowDate.setFullYear(nowDate.getFullYear() + yearCount);
    nowDate.setMonth(nowDate.getMonth() + monthCount);
    setDate(nowDate);
  };

  return (
    <MonthBox>
      <div>
        <ArrowBtn onClick={() => changeMonth(-1)}>
          <Arrowimg />
        </ArrowBtn>
        <div className="timeBox">
          <span>{date.getFullYear()}</span>
          <span>{monthArr[date.getMonth()]}</span>
        </div>

        <ArrowBtn onClick={() => changeMonth(1)}>
          <ReverseArrow />
        </ArrowBtn>
      </div>
    </MonthBox>
  );
}
