import styled from "styled-components";

import Day from "../Components/Month-day";
import Arrow from "../Img/ep_arrow-right-bold.svg";

const Container = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const MonthBox = styled.div`
  width: 100%;
  height: 5%;
  display: flex;
  justify-content: center;
  align-items: center;

  span {
    font-size: var(--header-size);
    font-weight: 600;
  }
`;

const ArrowBtn = styled.img`
  path {
    fill: blue;
  }
`;

const DaysBox = styled.div`
  width: 100%;
  height: 95%;
  display: flex;
  flex-direction: column;
`;

const DayWeekBox = styled.ul`
  display: flex;
  flex-grow: 1;
  width: 100%;
  height: 1%;
  li {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    background-color: var(--whiteblue);
  }
`;

const WeekBox = styled.ul`
  width: 100%;
  flex-grow: 1;
  display: flex;
`;

export default function Month() {
  const monthList = (nowDate: Date) => {
    const nowYear = nowDate.getFullYear();
    const nowMonth = nowDate.getMonth();

    const dayOneWeek = new Date(nowYear, nowMonth, 1).getDay();
    // const dayLastWeek = new Date(nowYear, nowMonth + 1, 0).getDay();
    let weekArr: number[] = [];
    const result: [number[]] = [[]];
    result.pop();
    //const prevMonthEnd = new Date(nowYear, nowMonth, 0).getDate();
    const nowMonthEnd = new Date(nowYear, nowMonth + 1, 0).getDate();

    for (let i = dayOneWeek - 1; i >= 0; i--) {
      weekArr.push(0);
    }

    for (let i = 1; i <= nowMonthEnd; i++) {
      weekArr.push(new Date(nowYear, nowMonth, i).getDate());

      if (weekArr.length === 7) {
        result.push(weekArr);
        weekArr = [];
      }
    }

    if (weekArr.length) {
      while (weekArr.length < 7) {
        weekArr.push(0);
      }
      result.push(weekArr);
    }

    return result;
  };

  const date = new Date();
  const allDay: [number[]] = monthList(date);
  const dayWeek: string[] = ["SUN", "MON", "TUE", "WED", "THU", "FRI", "SAT"];
  return (
    <Container>
      <MonthBox>
        <button>
          <ArrowBtn src={Arrow}></ArrowBtn>
        </button>
        <span>January</span>
      </MonthBox>

      <DaysBox>
        <DayWeekBox>
          {dayWeek.map((item: string, key: number) => {
            return (
              <li key={key}>
                <span>{item}</span>
              </li>
            );
          })}
        </DayWeekBox>
        {allDay.map((item: number[], idx: number) => {
          return (
            <WeekBox key={idx}>
              {item.map((day: number, key: number) => {
                return <Day day={day} key={key}></Day>;
              })}
            </WeekBox>
          );
        })}
      </DaysBox>
    </Container>
  );
}
