import styled from "styled-components";
import Plus from "../Img/mingcute_add-fill.svg";
import TodoLi from "../Components/Main-TodoLI";

interface TODOOBJ {
  name: string;
  tag: string;
  color: string;
}
const Container = styled.div`
  width: 100%;
  height: 100%;
  border: 1px solid black;
  display: flex;
  justify-content: space-between;
`;

const TodoContainer = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-direction: column;
  gap: 3rem;
  padding: 2rem 3rem;
`;

const ScheduleBox = styled.div`
  width: 100%;
`;

const TodoBox = styled.div`
  width: 100%;
  height: 50%;
  min-height: 50%;
  border-radius: 30px;
  background: rgba(244, 244, 246, 1);
  padding: 2rem;

  .Header {
    display: flex;
    justify-content: space-between;
    margin-bottom: 1.5rem;

    span {
      font-weight: 600;
      font-size: 20px;
    }
  }

  ul {
    display: flex;
    width: 100%;
    height: 100%;
    gap: 10px;
    flex-direction: column;
    overflow: scroll;
    overflow-y: scroll;
    scroll-behavior: smooth;
  }
`;

export default function Main() {
  const Tododata: TODOOBJ[] = [
    { name: "algorithm", tag: "Homework", color: "yellow" },
    { name: "pen", tag: "What to buy", color: "blue" },
    { name: "pen", tag: "What to buy", color: "blue" },
    { name: "pen", tag: "What to buy", color: "blue" },
    { name: "pen", tag: "What to buy", color: "blue" },
    { name: "pen", tag: "What to buy", color: "blue" },
  ];

  return (
    <Container>
      <TodoContainer>
        <TodoBox>
          <div className="Header">
            <span>To do</span>
            <button>
              <img src={Plus}></img>
            </button>
          </div>
          <ul>
            {Tododata.map((item: TODOOBJ, key: number) => {
              return <TodoLi todo={item} key={key} />;
            })}
          </ul>
        </TodoBox>
        <TodoBox></TodoBox>
      </TodoContainer>
      <ScheduleBox></ScheduleBox>
    </Container>
  );
}
