import styled from "styled-components";

const DayBox = styled.li`
  list-style: none;
  width: 100%;
  height: 100%;
  border: 1px solid var(--line-gray);
  padding: 0.5rem;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;

  .numberBox {
    width: 100%;
    height: auto;
    display: flex;
    justify-content: start;
    align-items: center;
    padding: 0;
    .number {
      display: flex;
      justify-content: center;
      align-items: center;
      width: 24px;
      height: 24px;
    }
  }

  .TodoBox {
    width: 100%;
    height: 100%;
    border: 1px solid black;
    display: flex;
    justify-content: center;
  }

  &:hover {
    background: var(--whiteblue);
  }
`;

const TodayBox = styled(DayBox)`
  .number {
    color: white;
    text-align: center;
    font-size: var(--normal-size);
    background-color: var(--skyblue);
    border-radius: 50%;
  }
`;

export default function Day(props: { day: number; today: number }) {
  return props.day !== 0 ? (
    props.today !== props.day ? (
      <DayBox>
        <div className="numberBox">
          <div className="number">{props.day}</div>
        </div>
        <div className="TodoBox"></div>
      </DayBox>
    ) : (
      <TodayBox>
        <div className="numberBox">
          <div className="number">{props.day}</div>
        </div>
        <div className="TodoBox"></div>
      </TodayBox>
    )
  ) : (
    <DayBox></DayBox>
  );
}
