import styled from "styled-components";
import { useState } from "react";

import XBtn from "../Img/ph_x-bold.svg";

const Contatiner = styled.div`
  width: 100%;
  height: auto;
  display: flex;
  flex-direction: column;
  padding: 1rem;
  border-radius: 20px;
  background: white;
  box-shadow: 5px 5px 5px 5px var(--line-gray);
`;
const HeaderDiv = styled.div`
  width: 100%;
  height: 30px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: end;
  .Ximg {
    width: 25px;
    height: 25px;
  }
`;
const ModalInput = styled.input`
  width: 100%;
  height: auto;
  border-bottom: 1px solid var(--light-gray);
  padding: 10px;
  background: none;
`;

const SelectBox = styled.div`
  width: 100%;
  height: auto;
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
`;
const SelectContainer = styled.div`
  width: 100%;
  height: auto;
  padding: 1rem 1rem 1rem 0.5rem;
  border-bottom: 1px solid var(--light-gray);

  .tagPick {
    box-shadow: 5px 5px 5px 5px var(--line-gray);
  }
`;

const TagColorBox = styled.div`
  width: 24px;
  height: 24px;
  background: ${(p) => p.color || "var(--line-gray)"};
`;

export default function TagPicker() {
  const [isOpen, setIsOpen] = useState<boolean>(false);
  const [boxColor, setBoxColor] = useState<string>("");
  const arr = ["red", "blue", "black", "purple"];

  return (
    <Contatiner>
      <HeaderDiv>
        <button onClick={() => setIsOpen(!isOpen)}>
          <img className="Ximg" src={XBtn}></img>
        </button>
      </HeaderDiv>
      <ModalInput placeholder="태그 이름"></ModalInput>
      <SelectContainer>
        <span>태그 색상</span>
        <SelectBox>
          <div className="tagPick">
            <TagColorBox
              color={boxColor}
              onClick={() => setIsOpen(!isOpen)}
            ></TagColorBox>
          </div>

          {arr.map((item: string, key: number) => (
            <TagColorBox
              color={item}
              key={key}
              onClick={() => {
                setBoxColor(item);
                setIsOpen(!isOpen);
              }}
            ></TagColorBox>
          ))}
        </SelectBox>
      </SelectContainer>
    </Contatiner>
  );
}
