import styled from "styled-components";
import Day from "../Components/Month-day";
const WeekBox = styled.ul`
  width: 100%;
  flex-grow: 1;
  display: flex;
`;

type DataHoliday = {
  dateName: string;
  isHoliday: string;
};
interface Prop {
  week: number[];
  idx: number;
  dayArr: number[];
  nowYear: number;
  nowMonth: number;
  isHoliday: number[];
  holiday: DataHoliday[];
}

export default function Week(props: Prop) {
  const { week, idx, dayArr, nowYear, nowMonth, isHoliday, holiday } = props;
  return (
    <WeekBox>
      {props.week.map((day: number, key: number) => {
        const index: number = isHoliday.indexOf(day);
        return (
          <Day
            day={day}
            getday={props.dayArr[key]}
            nowYear={nowYear}
            month={nowMonth + 1}
            key={key}
            holiday={index === -1 ? undefined : holiday[index]}
          ></Day>
        );
      })}
    </WeekBox>
  );
}
