import styled, { keyframes } from "styled-components";
import { useState } from "react";

const Container = styled.div`
  width: 100%;
  height: auto;
  display: flex;
  justify-content: center;
  align-items: center;
  box-shadow: 5px 5px 5px 5px var(--line-gray);
  padding: 1rem;
  gap: 2rem;
`;

const StartDiv = styled.div`
  width: 100%;
  height: 40px;
`;

const UlKeyframe = keyframes`
from {
  opacity: 0;
  transform: translate3d(0, -100%, 0);
}

to {
  opacity: 1;
  transform: translate3d(0, 0, 0);
}
}
`;

const SelectBox = styled.ul`
  width: 100%;
  height: 100px;
  overflow-y: scroll;
  display: flex;
  flex-direction: column;
  gap: 1rem;
  background: white;
  padding: 1rem;
  animation: ${UlKeyframe} 0.2s ease-in-out;

  li {
    cursor: pointer;
  }
`;

const TimeInput = styled.input<{ bool: boolean }>`
  width: 100%;
  padding: 1rem;
  outline: none;
  text-align: center;
  transition: background-color 0.5s ease;
  &:hover {
    background: var(--whiteblue);
  }

  &:disabled {
    background: var(--whiteblue);

`;

export default function TimePicker() {
  const [isOpen, setIsOpen] = useState<boolean>(false);
  const [endOpen, setEndOpen] = useState<boolean>(false);
  const timeArr = Array.from({ length: 24 }, (_, i) => i + 1);
  return (
    <Container>
      <StartDiv onClick={() => setIsOpen(!isOpen)}>
        <TimeInput
          bool={isOpen}
          disabled={isOpen}
          type="text"
          readOnly
          placeholder="시작 시간"
          value={`${isOpen}`}
        ></TimeInput>
        {isOpen ? (
          <SelectBox>
            {timeArr.map((item: number, key: number) => {
              return (
                <li
                  value={String(item)}
                  key={key}
                  onClick={() => setIsOpen(!isOpen)}
                >
                  {`${item} : 00`}
                </li>
              );
            })}
          </SelectBox>
        ) : null}
      </StartDiv>
      <StartDiv onClick={() => setEndOpen(!endOpen)}>
        <TimeInput
          bool={endOpen}
          type="text"
          readOnly
          placeholder="시작 시간"
          value={`${isOpen}`}
        ></TimeInput>
        {endOpen ? (
          <SelectBox>
            {timeArr.map((item: number, key: number) => {
              return (
                <li
                  value={String(item)}
                  key={key}
                  onClick={() => setEndOpen(!endOpen)}
                >
                  {`${item} : 00`}
                </li>
              );
            })}
          </SelectBox>
        ) : null}
      </StartDiv>
    </Container>
  );
}
