import styled from "styled-components";
import { Swiper, SwiperSlide } from "swiper/react";
import Xbtn from "../Img/ph_x-bold.svg";
// import axios from "axios";

const BackgroundBox = styled.div`
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  background: transparent;
  position: fixed;
  z-index: 100;
  overflow: hidden;
`;

const Container = styled.div`
  width: 450px;
  max-height: 500px;
  height: auto;
  display: flex;
  flex-direction: column;
  justify-content: center;

  background: white;
  border-radius: 20px;
  z-index: 110;
  position: fixed;
  padding: 2rem;
  gap: 1rem;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  margin: auto;
  box-shadow: 5px 5px 5px 5px var(--line-gray);
`;

const ModalInputBox = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 3rem;
`;
const HeaderDiv = styled.div`
  width: 100%;
  height: 30px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: end;
  .Ximg {
    width: 25px;
    height: 25px;
  }
`;

const ModalInput = styled.input`
  width: 100%;
  height: auto;
  border-bottom: 1px solid var(--light-gray);
  padding: 10px;
  background: none;
`;

const ModalTime = styled.div`
  width: 100%;
  height: 100%;
  border-bottom: 1px solid var(--light-gray);
  padding: 10px;
  background: none;
`;

const TimeBlock = styled.div`
  width: 40%;
  height: 50%;
  background: var(--line-gray);
  display: flex;
  flex-direction: column;

  box-shadow: 5px 5px 5px 5px var(--line-gray);
  padding: 1rem;

  .timeBtn {
    width: 100%;
    height: 1000px;
    white-space: nowrap;
    border-bottom: 1px solid black;
  }
`;

interface Prop {
  modal: boolean;
  setModal: React.Dispatch<React.SetStateAction<boolean>>;
}

export default function AddTodo(props: Prop) {
  const { modal, setModal } = props;
  const timeArr = Array.from({ length: 24 }, (_, i) => i + 1);

  return (
    <>
      <BackgroundBox onClick={() => setModal(!modal)}></BackgroundBox>
      <Container>
        <HeaderDiv>
          <button onClick={() => setModal(false)}>
            <img src={Xbtn} className="Ximg"></img>
          </button>
        </HeaderDiv>
        <ModalInputBox>
          <ModalInput placeholder="제목" />
          <ModalTime>
            <TimeBlock>
              <Swiper
                direction={"vertical"}
                slidesPerView={3}
                mousewheel
                loopAdditionalSlides={5}
                slideToClickedSlide
                centeredSlides
                onSlideChange={(swiper) => console.log(swiper.realIndex)}
              >
                {timeArr.map((item: number, key: number) => {
                  return (
                    <SwiperSlide key={key}>
                      <button className="timeBtn">
                        {(item < 12 ? "오전 0" + item : "오후 " + (item - 12)) +
                          " : 00"}
                      </button>
                    </SwiperSlide>
                  );
                })}
              </Swiper>
            </TimeBlock>
          </ModalTime>
        </ModalInputBox>
      </Container>
    </>
  );
}
