import styled from "styled-components";
import axios from "axios";
import Week from "../Components/Month-week";
import MonthHead from "../Components/Month-head";

import { useState, useLayoutEffect } from "react";

const Container = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const DaysBox = styled.div`
  width: 100%;
  height: 95%;
  display: flex;
  flex-direction: column;
`;

const DayWeekBox = styled.ul`
  display: flex;
  flex-direction: row;
  width: 100%;
  height: 10%;

  li {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    background-color: var(--whiteblue);

    span {
      font-weight: 600;
      font-size: 18px;
    }
  }
`;

export default function Month() {
  interface Holiday {
    dateKind: string;
    dateName: string;
    isHoliday: string;
    locdate: number;
    seq: number;
  }
  const [date, setDate] = useState<Date>(new Date());
  const [holidata, setHoliDate] = useState<Holiday[] | undefined>([]);

  const dayWeek: string[] = ["SUN", "MON", "TUE", "WED", "THU", "FRI", "SAT"];

  return (
    <Container>
      <MonthHead date={date} setDate={setDate} setHoliDate={setHoliDate} />
      <DaysBox>
        <DayWeekBox>
          {dayWeek.map((item: string, key: number) => {
            return (
              <li key={key}>
                <span>{item}</span>
              </li>
            );
          })}
        </DayWeekBox>
        <Week date={date} holidata={holidata} />
      </DaysBox>
    </Container>
  );
}
