import styled from "styled-components";
import ReactDatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { ko } from "date-fns/locale";
import Xbtn from "../Img/ph_x-bold.svg";
// import axios from "axios";

const BackgroundBox = styled.div`
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  background: transparent;
  position: fixed;
  z-index: 100;
  overflow: hidden;
`;

const Container = styled.div`
  width: 450px;
  max-height: 500px;
  height: auto;
  display: flex;
  flex-direction: column;
  justify-content: center;

  background: white;
  border-radius: 20px;
  z-index: 110;
  position: fixed;
  padding: 2rem;
  gap: 1rem;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  margin: auto;
  box-shadow: 5px 5px 5px 5px var(--line-gray);
`;

const ModalInputBox = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 3rem;
`;
const HeaderDiv = styled.div`
  width: 100%;
  height: 30px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: end;
  .Ximg {
    width: 25px;
    height: 25px;
  }
`;

const ModalInput = styled.input`
  width: 100%;
  height: auto;
  border-bottom: 1px solid var(--light-gray);
  padding: 10px;
  background: none;
`;

const ModalTime = styled.div`
  width: 100%;
  height: 100%;
  border-bottom: 1px solid var(--light-gray);
  padding: 10px;
  background: none;
`;

const TimeBlock = styled.div`
  width: 40%;
  height: 50%;
  background: var(--line-gray);
  display: flex;
  flex-direction: column;
  align-items: center;
  overflow-y: scroll;
  box-shadow: 5px 5px 5px 5px var(--line-gray);
  padding: 1rem;
  gap: 3rem;

  button {
    width: 100%;
    height: auto;
    white-space: nowrap;
    border-bottom: 1px solid black;
  }
`;

const StyledDatePicker = styled(ReactDatePicker)`
  width: 100%;
  height: 100%;
  text-align: center;
  button {
    width: 40px;
    height: 40px;
  }
  .react-datepicker__tap-loop {
    background: red;
  }
`;

const PrevButton = styled.button`
  background-color: #007bff;
  border: none;
  border-radius: 50%;
  color: white;
  padding: 8px;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #0056b3;
  }
`;

// 다음 달로 이동하는 버튼
const NextButton = styled(PrevButton)``;
interface Prop {
  modal: boolean;
  setModal: React.Dispatch<React.SetStateAction<boolean>>;
}

export default function AddTodo(props: Prop) {
  const { modal, setModal } = props;
  // const timeArr = Array.from({ length: 24 }, (_, i) => i + 1);
  const date = new Date();
  return (
    <>
      <BackgroundBox onClick={() => setModal(!modal)}></BackgroundBox>
      <Container>
        <HeaderDiv>
          <button onClick={() => setModal(false)}>
            <img src={Xbtn} className="Ximg"></img>
          </button>
        </HeaderDiv>
        <ModalInputBox>
          <ModalInput placeholder="제목" />
          <ModalTime>
            <TimeBlock>
              <StyledDatePicker
                locale={ko}
                dateFormat="yyyy.MM.dd"
                selected={date}
                placeholderText="시작 날짜"
                closeOnScroll={true}
                onChange={() => console.log()}
                renderCustomHeader={({ decreaseMonth, increaseMonth }) => (
                  <div style={"display:flex"}>
                    <PrevButton onClick={decreaseMonth}></PrevButton>
                    <span>{date.toLocaleDateString()}</span>
                    <NextButton onClick={increaseMonth}></NextButton>
                  </div>
                )}
              />
            </TimeBlock>
          </ModalTime>
        </ModalInputBox>
      </Container>
    </>
  );
}
