import styled from "styled-components";
import Xbtn from "../Img/ph_x-bold.svg";
// import axios from "axios";

const BackgroundBox = styled.div`
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  background: transparent;
  position: fixed;
  z-index: 100;
  overflow: hidden;
`;

const Container = styled.div`
  width: 450px;
  height: 550px;
  display: flex;
  flex-direction: column;
  justify-content: center;

  background: #f4f4f6;
  border-radius: 20px;
  z-index: 110;
  position: fixed;
  padding: 2rem;
  gap: 1rem;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  margin: auto;
`;

const ModalHeaderBox = styled.div`
  width: 100%;
  height: 100%;
`;

const ModalInputBox = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;
const HeaderDiv = styled.div`
  width: 100%;
  height: 30px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const ModalInput = styled.input`
  width: 100%;
  height: auto;
  border-bottom: 1px solid black;
  padding: 10px;
`;

interface Prop {
  modal: boolean;
  setModal: React.Dispatch<React.SetStateAction<boolean>>;
}

export default function AddTodo(props: Prop) {
  const { modal, setModal } = props;

  return (
    <>
      <BackgroundBox onClick={() => setModal(!modal)}></BackgroundBox>
      <Container>
        <HeaderDiv>
          <img src={Xbtn}></img>
        </HeaderDiv>
        <ModalInput placeholder="제목" />
        <ModalInput placeholder="제목" />
        <ModalInput placeholder="제목" />
      </Container>
    </>
  );
}
