import styled from "styled-components";
import { useState } from "react";

const Container = styled.div`
  width: 100%;
  height: auto;
  display: flex;
  box-shadow: 5px 5px 5px 5px var(--line-gray);
  padding: 1rem;
`;

const StartDiv = styled.div`
  width: 100%;
  height: 40px;
`;

const SelectBox = styled.ul`
  width: 50%;
  height: 40px;
  overflow-y: scroll;
  li {
    overflow-y: scroll;
  }
`;

export default function TimePicker() {
  const [isOpen, setIsOpen] = useState<boolean>(false);
  const timeArr = Array.from({ length: 24 }, (_, i) => i + 1);
  return (
    <Container>
      <StartDiv onClick={() => setIsOpen(true)}>
        시작 시간
        <SelectBox>
          {timeArr.map((item: number, key: number) => {
            return (
              <li value={String(item)} key={key}>
                {`${item} : 00`}
              </li>
            );
          })}
        </SelectBox>
      </StartDiv>
    </Container>
  );
}
