import styled from "styled-components";
import { useNavigate } from "react-router";

interface DayBoxType {
  onClick?: (e: React.MouseEvent) => void;
}

const DayBox = styled.li<DayBoxType>`
  width: 100%;
  height: 100%;
  border: 1px solid var(--line-gray);
  padding: 0.5rem;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  gap: 5%;

  .numberBox {
    width: 100%;
    height: 24px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding-right: 10px;
  }

  .TodoBox {
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: start;
    flex-direction: column;
    padding: 0.5rem;
    gap: 1rem;
    overflow-y: scroll;
    flex: 1 1 0;

    li {
      width: 100%;
      height: 12px;
      display: flex;
      gap: 1rem;
      .colorBox {
        height: 100%;
        width: 12px;
        background-color: ${(prop) => prop.color || "var(--line-gray)"};
      }
      span {
        font-size: 12px;
      }
    }
  }

  &:hover {
    background: var(--whiteblue);
  }
`;

const TodayBox = styled(DayBox)`
  .number {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 24px;
    height: 24px;
    color: white;
    font-size: var(--normal-size);
    background-color: var(--skyblue);
    border-radius: 50%;
  }
`;

interface NumberProp {
  color: boolean;
  background: string;
}

const Number = styled.div<{ prop: NumberProp }>`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 24px;
  height: 24px;
  color: ${(prop) => prop.color || "black"};
  font-size: var(--normal-size);
  background-color: ${(prop) => "white" || "var(--skyblue)"};
  border-radius: 50%;
`;

type DataHoliday = {
  dateName: string;
  isHoliday: string;
};

export default function Day(props: {
  month: number;
  holiday: DataHoliday | undefined;
  day: number;
  nowYear: number;
  getday: number;
}) {
  const navi = useNavigate();
  const date = new Date();

  function todayFunc(): boolean {
    return (
      date.getFullYear() === props.nowYear &&
      date.getMonth() === props.month &&
      date.getDate() === props.day
    );
  }

  return day === 0 ? (
    <DayBox onClick={() => navi(`/today/${props.month}/${props.day}`)}>
      <div className="numberBox">
        <Number
          color={
            props.getday === 0 || props.getday === 6
              ? props.getday === 0
                ? "red"
                : "blue"
              : "black"
          }
          background={todayFunc() ? "var(--skyblue)" : "none"}
        >
          {props.day}
        </Number>
        <div>{props.holiday ? props.holiday.dateName : null}</div>
      </div>
      <ul className="TodoBox">
        <li>
          <div className="colorBox"></div> <span>algorithm</span>
        </li>
        <li>
          <div className="colorBox"></div> <span>algorithm</span>
        </li>
        <li>
          <div className="colorBox"></div> <span>algorithm</span>
        </li>
        <li>
          <div className="colorBox"></div> <span>algorithm</span>
        </li>
        <li>
          <div className="colorBox"></div> <span>algorithm</span>
        </li>
        <li>
          <div className="colorBox"></div> <span>algorithm</span>
        </li>
        <li>
          <div className="colorBox"></div> <span>algorithm</span>
        </li>
      </ul>
    </DayBox>
  ) : (
    <DayBox></DayBox>
  );
}
