import styled from "styled-components";
// import axios from "axios";
import { useEffect, useState } from "react";

const BackgroundBox = styled.div`
  width: 100%;
  height: 100%;
  background: transparent;
  position: absolute;
  border: 1px solid black;
`;

interface ModalStyle {
  left: string;
}

const Container = styled.div<ModalStyle>`
  width: 500px;
  height: 300px;
  display: flex;
  flex-direction: column;
  justfy-content: center;
  align-items: center;
  background: var(--whiteblue);
  border-radius: 20px;
  position: absolute;
  top: 6%;
  left: ${(prop) => prop.left || "36%"};
`;

interface Prop {
  modal: boolean;
  setModal: React.Dispatch<React.SetStateAction<boolean>>;
}

export default function AddTodo(props: Prop) {
  const [width, setWidth] = useState<number>(window.innerWidth);
  const { modal, setModal } = props;
  const resizeWidth: number = Number(Math.round(width * 0.65));
  const handleResize = () => {
    setWidth(window.innerWidth);
  };

  useEffect(() => {
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, [width]);
  console.log(resizeWidth);
  return (
    <BackgroundBox onClick={() => setModal(!modal)}>
      <Container left={`${resizeWidth}`}></Container>
    </BackgroundBox>
  );
}
