import styled from "styled-components";
import { ReactComponent as Check } from "../Img/cil_check-alt.svg";

interface TODOOBJ {
  name: string;
  tag: string;
  color: string;
}

const TodoList = styled.li`
  display: flex;
  width: 100%;
  height: 100px;
  min-height: 80px;
  background: var(--white);
  border-radius: 30px;
  align-items: center;
  gap: 2rem;
  padding: 0 2rem;
`;

const CheckBtn = styled.button`
  width: auto;
  height: auto;

  display: flex;
  jutify-content: center;
  align-items: center;
  border-radius: 50%;
  border: 1.5px solid black;
  path {
    fill: black;
  }

  & :hover {
    background: black;
    border-radius: 50%;
    path {
      fill: white;
    }
  }
`;

const CheckedBtn = styled(CheckBtn)`
  background-color: black;
  path {
    fill: white;
  }

  & :hover {
    background: white;
    border-radius: 50%;
    path {
      fill: black;
    }
  }
`;

const CheckImg = styled(Check)`
  width: auto;
  height: auto;
  padding: 0.1rem;
`;

const ListBody = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 1rem;
  span {
    font-weight: 600;
    font-size: 20px;
  }

  .tag {
    display: flex;
    gap: 1rem;
    span {
      font-size: var(--small-size);
    }
  }
`;

const ColorBox = styled.div`
  width: 20px;
  background-color: ${(prop) => prop.color || "var(--line-gray)"};
`;

export default function TodoLi(props: { todo: TODOOBJ; trigger: boolean }) {
  const { name, tag, color } = props.todo;
  return (
    <TodoList>
      {trigger ? (
        <CheckBtn>
          <CheckImg />
        </CheckBtn>
      ) : (
        <CheckedBtn>
          <CheckImg />
        </CheckedBtn>
      )}
      <CheckBtn>
        <CheckImg />
      </CheckBtn>
      <ListBody>
        <span>{name}</span>
        <div className="tag">
          <ColorBox color={color}></ColorBox>
          {tag}
        </div>
      </ListBody>
    </TodoList>
  );
}
