import styled from "styled-components";
import { useState } from "react";

const Container = styled.div`
  width: 100%;
  height: auto;
  display: flex;
  justify-content: center;
  align-items: center;
  box-shadow: 5px 5px 5px 5px var(--line-gray);
  padding: 1rem;
`;

const StartDiv = styled.div`
  width: 100%;
  height: 40px;
`;

const SelectBox = styled.ul`
  width: 100%;
  height: 100px;
  overflow-y: scroll;
  display: flex;
  flex-direction: column;
  gap: 1rem;
  background: white;
  border-radius: 20px;
  padding: 1rem;

  & :active {
    box-shadow: 5px 5px 5px 5px var(--line-gray);
  }

  li {
    cursor: pointer;
  }
`;

export default function TimePicker() {
  const [isOpen, setIsOpen] = useState<boolean>(false);
  const [endOpen, setEndOpen] = useState<boolean>(false);
  const timeArr = Array.from({ length: 24 }, (_, i) => i + 1);
  return (
    <Container>
      <StartDiv onClick={() => setIsOpen(!isOpen)}>
        시작 시간
        {isOpen ? (
          <SelectBox>
            {timeArr.map((item: number, key: number) => {
              return (
                <li
                  value={String(item)}
                  key={key}
                  onClick={() => setIsOpen(!isOpen)}
                >
                  {`${item} : 00`}
                </li>
              );
            })}
          </SelectBox>
        ) : null}
      </StartDiv>
      <StartDiv onClick={() => setEndOpen(!endOpen)}>
        시간
        {endOpen ? (
          <SelectBox>
            {timeArr.map((item: number, key: number) => {
              return (
                <li
                  value={String(item)}
                  key={key}
                  onClick={() => setEndOpen(!endOpen)}
                >
                  {`${item} : 00`}
                </li>
              );
            })}
          </SelectBox>
        ) : null}
      </StartDiv>
    </Container>
  );
}
